mapper.select:
	SELECT seq, reviewid, reviewname, subject, content, ref, grade, image1, hit, likes
    FROM (
        SELECT rownum rn, tt.* 
        FROM (SELECT * FROM odigafoodreview ORDER BY seq DESC) tt
    )
    WHERE rn BETWEEN #{startNum} AND #{endNum}


SELECT * 
FROM (
    SELECT f.*, ROWNUM AS rnum 
    FROM (
        SELECT * 
        FROM odigafoodreview 
        WHERE content LIKE '%' || #{searchTerm} || '%' 
           OR subject LIKE '%' || #{searchTerm} || '%' 
        ORDER BY logtime DESC
    ) f
    WHERE ROWNUM <= #{endNum}
) 
WHERE rnum > #{startNum}


SELECT * 
FROM (
    SELECT f.*, ROWNUM AS rnum 
    FROM (
        SELECT * 
        FROM odigafoodreview 
        WHERE subject LIKE '%' || #{searchTerm} || '%' 
        ORDER BY logtime DESC
    ) f
    WHERE ROWNUM <= #{endNum}
) 
WHERE rnum > #{startNum}


